import React, { useState, useEffect } from "react";
import itemPic_1 from './images/item_1.jpg'
import itemPic_2 from './images/item_2.jpg'
import itemPic_3 from './images/item_3.png'
import itemPic_4 from './images/item_4.jpg'
import itemPic_5 from './images/item_5.jpg'
import itemPic_6 from './images/item_6.jpg'

const Cart = (props)=>{

    const [cart, setCart] = useState([]);

    useEffect(() => {
        formCartArray();

    }, [props.items,cart,formCartArray]);
    let cartArr=[]


    function remove(name){
        console.log("Will remove "+name);
        console.log(props.items.item_1.name);
        if(name===props.items.item_1.name){
            console.log("xd")
            props.addItem({
                item_1:{
                  name:"Vestir Plain White Tee",
                  quantity:props.items.item_1.quantity-1,
                  price:9.95,
                },
                item_2:{
                  name:"Vestir Plain Black Tee",
                  quantity:props.items.item_2.quantity,
                  price:9.95,
                },
                item_3:{
                  name:"Vestir Gray Polo Men",
                  quantity:props.items.item_3.quantity,
                  price:19.95,
                },
                item_4:{
                  name:"Vestir Black Blazer Men",
                  quantity:props.items.item_4.quantity,
                  price:49.95,
                },
                item_5:{
                  name:"Vestir Black Blazer Women",
                  quantity:props.items.item_5.quantity,
                  price:45.95,
                },
                item_6:{
                  name:"Vestir Black Skirt Women",
                  quantity:props.items.item_6.quantity,
                  price:29.95,
                }, 
              });
        }
        console.log("----------------------")
        console.log(props.items.item_1.quantity)
        console.log("cart is "+cart)
        console.log("its now "+props.items.item_1.quantity)
        fuckCartArray()
    }
    function fuckCartArray(){
        console.log("double index test"+cartArr[0][1])
        //cartArr[0][1]

       // setCart(cartArr)
    }
    let listItems = cart.map((number) =>
    <div className="cartItemList">
                            <div className="toCheckOut">
                                <div className="checkOutImage"><img src={number[4]} ></img></div>

                                <div className="checkOutDetails">
                                    <div className="itemName">{number[0]}</div>
                                    <div className="itemNameDetails">{cart.indexOf(number)}</div>
                                    <button className="btnRemove" onClick={()=>remove(number[0])}>Remove</button>
                                </div>
                                <div className="checkOutQuantity">
                                    {number[1]}
                                </div>
                                <div className="checkOutSubtotal">{"$"+

                                Math.round(((number[2]*number[1]) + Number.EPSILON) * 100) / 100
                                }</div>
                            </div>

                        </div>  

    );

    function summary(){
        let total=0;
        cart.map((number)=>
        total+=number[1]*number[2]
        )
        return Math.round((total + Number.EPSILON) * 100) / 100
    }
    return (
        <div>

            <header>{props.cartItem_1[1]}</header>
            <div className="container">
                <div className="cart">
                    <div className="tableHead">
                        <div className="thItems">Items</div>
                        <div className="thQuantity">Quantity</div>
                        <div className="thSubtotal">Subtotal</div>

                    </div>
                    <div className="cartItems">
                    {listItems}
                    </div>
                    <div>{summary()}</div>


                </div>
            </div>
        </div>
    )
}
export default Cart
